<?php

if (!function_exists('create_permission')) {
    /**
     * @param string $label
     *
     * @return \App\Models\Permission
     */
    function create_permission(string $label)
    {
        return create('App\Models\Permission', [
            'label'       => $label,
            'title'       => 'Test Title',
            'description' => 'This is test description for new permission',
            'delete_able' => false
        ]);
    }
}

if (!function_exists('create')) {
    /**
     * Clear the create factory
     *
     * @param string $class
     * @param array  $attribute
     * @param null   $times
     *
     * @return  mixed
     */
    function create(string $class, array $attribute = [], $times = null)
    {
        return factory($class, $times)->create($attribute);
    }
}

