if (!function_exists('arr_null')) {

    function arr_null(array $array, string $key)
    {
        if (array_key_exists($key, $array)) {
            $array[$key] = null;
        }
        return $array;
    }
}

if (!function_exists('jpeg_fake_base_64')) {
    /**
